import React from 'react';
import { Link } from 'react-router-dom';

function Landingscreen() {
  return (
    <div className='row landing justify-content-center'>
      <div className="col-md-6 my-auto text-center" style={{ borderRight: '8px solid white' }}>
        <img
          src="https://th.bing.com/th/id/OIP.DVvAgoVACPyAP4Ix3phhjQHaFc?w=230&h=180&c=7&r=0&o=5&dpr=1.5&pid=1.7"
          alt="Hotel Image"
          style={{ maxWidth: '100%', height: '100px' }}
        />
      </div>
      <div className="col-md-6 my-auto text-center" style={{ borderRight: '8px solid white' }}>
        <h2 style={{ color: 'white', fontSize: '30px' }}>
          Hotel Reservation System
        </h2>
        <p style={{ color: 'white', fontSize: '18px' }}>
          Your comfortability is our PRIORITY. Welcome to our hotel reservation system. Book your stay with ease and enjoy a delightful experience.
        </p>
        <Link to='/home'>
          <button className='btn landingbtn' style={{ color: 'blue' }}>
            Continue
          </button>
        </Link>
      </div>
    </div>
  );
}

export default Landingscreen;
